// Sample data for products
const products = [
  { id: 1, name: "Laptop", category: "electronics", price: 50000, rating: 4.5, img: "data:image/jpeg;base64," },
  { id: 2, name: "Shirt", category: "clothing", price: 1000, rating: 3.8, img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS0z1rl4rLrCl4daXg_FkGJaVwFFHUM1O1XhA&s" },
  { id: 3, name: "Smartphone", category: "electronics", price: 28000, rating: 4.2, img: "data:image/jpeg;base64," },
  { id: 4, name: "Sofa", category: "home", price: 60000, rating: 4.9, img: "data:image/jpeg;base64," },
  { id: 5, name: "Watch", category: "electronics", price: 5000, rating: 4.1, img: "data:image/jpeg;base64," },
  { id: 6, name: "One piece", category: "clothing", price: 2000, rating: 3.5, img: "https://m.media-amazon.com/images/I/51cPyGthChL._AC_UY1100_.jpg" },
];

// Function to render products based on filter
function renderProducts(filteredProducts) {
  const productList = document.getElementById("product-list");
  productList.innerHTML = ""; // Clear current product list
  filteredProducts.forEach(product => {
    const productItem = document.createElement("div");
    productItem.classList.add("product-item");
    productItem.innerHTML = `
      <img src="${product.img}" alt="${product.name}">
      <h3>${product.name}</h3>
      <p>${product.category}</p>
      <div class="price">Rs${product.price}</div>
      <div class="rating">${"★".repeat(Math.round(product.rating))}</div>
    `;
    productList.appendChild(productItem);
  });
}

// Function to filter products based on category and price range
function filterProducts() {
  const category = document.getElementById("category").value;
  const priceRange = document.getElementById("price").value;
  
  let filteredProducts = products;

  if (category !== "all") {
    filteredProducts = filteredProducts.filter(product => product.category === category);
  }

  if (priceRange !== "all") {
    if (priceRange === "under1000") {
      filteredProducts = filteredProducts.filter(product => product.price < 1000);
    } else if (priceRange === "1000to10000") {
      filteredProducts = filteredProducts.filter(product => product.price >= 1000 && product.price <= 10000);
    } else if (priceRange === "10000plus") {
      filteredProducts = filteredProducts.filter(product => product.price > 10000);
    }
  }

  sortProducts(filteredProducts); // Reapply sorting after filtering
}

// Function to sort products by rating or price
function sortProducts(filteredProducts = products) {
  const sortBy = document.getElementById("sort").value;

  if (sortBy === "rating") {
    filteredProducts.sort((a, b) => b.rating - a.rating);
  } else if (sortBy === "priceLowToHigh") {
    filteredProducts.sort((a, b) => a.price - b.price);
  } else if (sortBy === "priceHighToLow") {
    filteredProducts.sort((a, b) => b.price - a.price);
  }

  renderProducts(filteredProducts); // Render sorted products
}

// Initial rendering of products
renderProducts(products);
